BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "segments" (
	"name"	TEXT NOT NULL,
	PRIMARY KEY("name")
);
CREATE TABLE IF NOT EXISTS "markers" (
	"id"	INTEGER NOT NULL,
	"notes"	TEXT,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "markers_mutations" (
	"marker_id"	INTEGER NOT NULL,
	"mutation_name"	INTEGER NOT NULL,
	FOREIGN KEY("marker_id") REFERENCES "markers"("id") ON UPDATE CASCADE,
	FOREIGN KEY("mutation_name") REFERENCES "mutations"("name") ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "mutations" (
	"name"	TEXT NOT NULL,
	"type"	TEXT NOT NULL,
	"protein_name"	TEXT NOT NULL,
	PRIMARY KEY("name"),
	FOREIGN KEY("protein_name") REFERENCES "proteins"("name") ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "proteins" (
	"name"	TEXT NOT NULL,
	"segment_name"	TEXT NOT NULL,
	PRIMARY KEY("name"),
	FOREIGN KEY("segment_name") REFERENCES "segments"("name") ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "references" (
	"name"	TEXT NOT NULL,
	"segment_name"	TEXT NOT NULL,
	"sequence"	TEXT NOT NULL,
	PRIMARY KEY("name"),
	FOREIGN KEY("segment_name") REFERENCES "segments"("name") ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "papers" (
	"id"	TEXT NOT NULL,
	"title"	TEXT NOT NULL UNIQUE,
	"authors"	TEXT NOT NULL,
	"year"	INTEGER,
	"journal"	TEXT,
	"web_address"	TEXT,
	"doi"	TEXT,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "effects" (
	"name"	TEXT NOT NULL,
	PRIMARY KEY("name")
);
CREATE TABLE IF NOT EXISTS "markers_effects" (
	"marker_id"	INTEGER NOT NULL,
	"paper_id"	TEXT NOT NULL,
	"effect_name"	TEXT NOT NULL,
	"subtype"	TEXT NOT NULL,
	"in_vivo"	INTEGER,
	"in_vitro"	INTEGER,
	PRIMARY KEY("marker_id","paper_id","effect_name","subtype"),
	FOREIGN KEY("marker_id") REFERENCES "markers"("id"),
	FOREIGN KEY("paper_id") REFERENCES "papers"("id") ON UPDATE CASCADE,
	FOREIGN KEY("effect_name") REFERENCES "effects"("name") ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "annotations" (
	"start"	INTEGER NOT NULL,
	"end"	INTEGER NOT NULL,
	"protein_name"	TEXT NOT NULL,
	"reference_name"	TEXT NOT NULL,
	PRIMARY KEY("start","end","protein_name","reference_name"),
	FOREIGN KEY("reference_name") REFERENCES "references"("name") ON UPDATE CASCADE,
	FOREIGN KEY("protein_name") REFERENCES "proteins"("name") ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "mutation_mappings" (
	"id"	INTEGER NOT NULL UNIQUE,
	"mutation_name"	TEXT NOT NULL,
	"reference_name"	TEXT NOT NULL,
	"position"	INTEGER NOT NULL,
	"ref_seq"	TEXT NOT NULL,
	"alt_seq"	TEXT NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("reference_name") REFERENCES "references"("name") ON UPDATE CASCADE,
	FOREIGN KEY("mutation_name") REFERENCES "mutations"("name") ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "db_version" (
	"major"	INTEGER NOT NULL UNIQUE,
	"minor"	INTEGER NOT NULL UNIQUE,
	"date"	TEXT NOT NULL UNIQUE
);
CREATE VIEW markers_summary AS
  SELECT markers_mutations.marker_id,
      group_concat(markers_mutations.mutation_name) AS all_mutations,
      count(markers_mutations.mutation_name) as all_mutations_count
FROM markers_mutations 
GROUP BY markers_mutations.marker_id;
COMMIT;
